// 该文件用于创建Vuex中最为核心的store

// 引入Vuex
import Vuex from "vuex";
import Vue from "vue";

// 使用插件，必须写在该文件中，否则会因执行顺序而报错
Vue.use(Vuex)
// 准备actions，用于响应组件里面的动作
const actions = {}

// 准备mutations，用于加工数据
const mutations = {}

// 准备state，用于存储数据
const state = {
    sum: 0,  //当前的和:
}

// 创建并暴露store
export default new Vuex.Store({
    actions,
    mutations,
    state
})
